// ==UserScript==
// @name           MyAnimeList External Link Adder
// @author         Magnum357i - edited by emukus
// @namespace      emukus
// @homepage       https://github.com/emukus/
// @description    Adds external links in the end of the title
// @version        1.3
// @icon           https://cdn.myanimelist.net/images/favicon.ico
// @match          https://myanimelist.net/anime/*
// @run-at         document-end

// ==/UserScript==
(function() {

	'use strict';

	var sites =
	[
		{ name: 'Nyaa',           group: '1', status: 'open', url: 'https://nyaa.si/?f=0&c=1_2&q=%title%',                          icon: 'data:image/png;base64,' },
		{ name: 'AnimeTosho',     group: '1', status: 'open', url: 'https://animetosho.org/search?q=%title%',                       icon: 'data:image/png;base64,' },
		{ name: 'AniDex',		  group: '1', status: 'open', url: 'https://anidex.info/?q=%title%',								icon: 'data:image/png;base64,' },
	    { name: 'RuTracker',      group: '1', status: 'open', url: 'http://rutracker.org/forum/search_cse.php?q=%title%',           icon: 'data:image/x-icon;base64,'},
	    { name: 'Hi10Anime',      group: '1', status: 'open', url: 'https://hi10anime.com/?s=%title%',                              icon: 'data:image/x-icon;base64,'},
	    { name: 'PSARips',        group: '1', status: 'open', url: 'https://psa.wf/?s=%title%',                                     icon: 'data:image/png;base64,' },
        { name: 'Fora',           group: '1', status: 'open', url: 'https://fora.snahp.eu/search.php?keywords=%title%&sf=titleonly',           icon: 'data:image/x-icon;base64,'},
	    { name: 'AniList',        group: '2', status: 'open', url: 'https://anilist.co/search/anime?search=%title%',                icon: 'data:image/png;base64,' },
	    { name: 'AniDB',          group: '2', status: 'open', url: 'https://anidb.net/perl-bin/animedb.pl?show=animelist&adb.search=%title%',  icon: 'data:image/png;base64,'},
	    { name: 'LiveChart',      group: '2', status: 'open', url: 'https://www.livechart.me/search?q=%title%',                     icon: 'data:image/png;base64,' },
	    { name: 'IMDb',           group: '2', status: 'open', url: 'https://www.imdb.com/find/?q=%title%',                          icon: 'data:image/png;base64,'},
	    { name: 'Youtube',        group: '2', status: 'open', url: 'https://www.youtube.com/results?search_query=%title%+trailer',  icon: 'data:image/png;base64,' },
	    { name: 'TVTropes',       group: '2', status: 'open', url: 'https://tvtropes.org//pmwiki/search_result.php?q=%title%',      icon: 'data:image/png;base64,' },
	    { name: 'Google',         group: '2', status: 'open', url: 'https://www.google.com/search?q=%title%&gl=US',                 icon: 'data:image/png;base64,'},
	    { name: 'Google images',  group: '2', status: 'open', url: 'https://www.google.com/search?tbm=isch&q=%title%&gl=US',        icon: 'data:image/png;base64,'},
	    { name: 'Letterboxd',     group: '2', status: 'open', url: 'https://letterboxd.com/search/%title%',                         icon: 'data:image/png;base64,'},
	];

	var pageUrl = window.location.href;
	if (pageUrl.search(/myanimelist\.net\/anime\/\d+/) >= 0) {
		// Vars
	    var animeTitle = document.querySelector( '.title-name' ).firstChild.textContent.trim();
		var elTitle = document.querySelector( '.title-name' );

		// AnimeTosho için AniDB ID'si istisnası
		var anidbButton = document.querySelector('.external_links a[href*="anidb.net/perl-bin/animedb.pl?show=anime&aid="]');
		var g = getFirstItem(sites, 'name', 'AnimeTosho');
		if (anidbButton && g) {
			var anidbId = anidbButton.href.match(/\&aid=(\d+)/)[1];
			sites[g].url = "https://animetosho.org/series/" + anidbId;
		}

		animeTitle = animeTitle
			.replace( "(", "" )
			.replace( ")", "" )
			.replace( ":", " " )
			.replace( "-", " " )
	        .trim();
	    elTitle.innerHTML += html( 1, 15 ) + html( 2, 5 );
    }


	function html( $groupId, $left ) {
		var h = '';
		for( var i = 0, len = sites.length; i < len; i++ ) {
			var s = sites[i];

            if ( s.status == 'open' && s.group == $groupId ) {
                h += '<li style="padding: 0 4px;"><a href="' + url( s.url ) + '" target="_blank"><img style="width: 16px; height: 16px;" src="' + s.icon + '" title="' + s.name + '"><\/a></li>';
            }
		}
		return '<div style="display: inline; background: #eff4ff; padding: 1px 6px; margin-left: ' + $left + 'px; border: 1px solid #bebebe; border-radius: 3px;"><ul style="margin: 0; list-style-type: none; display: inline-flex; padding: 0; vertical-align: middle;">' + h + '</ul></div>';
	}

	function url( site ) {
        var t = encodeURIComponent( animeTitle );
		return site.replace( /%title%/, t );
	}

    function getFirstItem(input, key, value) {
        for(var i = 0; i < input.length; i++) {
            var obj = input[i];
            if(obj[key] == value) {return i;}
        } return null;
    }

})();
